mapper.update:
UPDATE employees
email=#{email}, phone_number=#{phone_number}, job_id = #{job_id},
WHERE employee_id=#{employee_id}

mapper.select:
SELECT * FROM employees e INNER JOIN department d ON e.department_id=d.department_id INNER JOIN jobs j ON e.job_id = j.job_id
DEPARTMENT_ID = #{department_id}
JOB_ID = #{job_id}
And UPPER(first_name) LIKE '%' || upper(#{first_name}) || '%'
