mapper.update:
UPDATE department SET department_name=#{department_name},
						manager_id=#{manager_id},
						location_id=#{location_id}
						WHERE department_id=#{department_id}

mapper.select:
select d.department_id, d.department_name, d.manager_id, e.last_name, l.location_id, l.city from departments d JOIN locations l ON(d.location_id = l.location_id) INNER JOIN employees e ON e.employee_id = d.manager_id
DEPARTMENT_NAME = #{department_name}
d.MANAGER_ID = #{manager_id}
